SELECT PEvent.publicID, Origin.time_value, Origin.evaluationMode, Origin.quality_usedPhaseCount, Magnitude.magnitude_value, Magnitude.type, Magnitude.stationCount, Origin.latitude_value, Origin.longitude_value, Origin.depth_value, EventDescription.text FROM Event, PublicObject AS PEvent, Origin, PublicObject AS POrigin, Magnitude, PublicObject AS PMagnitude, EventDescription WHERE (Event.type IS NULL OR Event.type='earthquake') AND Event._oid=PEvent._oid AND Origin._oid=POrigin._oid AND Magnitude._oid=PMagnitude._oid AND Event.preferredOriginID=POrigin.publicID AND Event.preferredMagnitudeID=PMagnitude.publicID AND Event._oid=EventDescription._parent_oid AND EventDescription.type='region name' AND Origin.evaluationMode = 'manual' AND Magnitude.magnitude_value >= 1.0 AND Magnitude.magnitude_value <= 9.0 AND Origin.depth_value >= 0 AND Origin.depth_value <= 1000 AND Origin.latitude_value <= 3.5 AND Origin.latitude_value >= -2.75 AND Origin.longitude_value >= 124.0 AND Origin.longitude_value <= 130.0 AND Origin.time_value >= '2022-12-20 15:12' AND Origin.time_value <= '2022-12-30 15:12' ORDER BY Origin.time_value DESC